con:config.script:
def defaultUnitOfMeasure = testRunner.testCase.getPropertyValue("defaultUnitOfMeasure")
if (defaultUnitOfMeasure == "piece"){
	testRunner.testCase.setPropertyValue( "defaultQuantityPrecision" , "0" )
} else if (defaultUnitOfMeasure == "kg"){
	testRunner.testCase.setPropertyValue( "defaultQuantityPrecision" , "3" )
} else {
	context.testRunner.fail("New unitOfMeasure is added $defaultUnitOfMeasure as default")
}

def supportedUnitsOfMeasure = testRunner.testCase.getPropertyValue("supportedUnitsOfMeasure")
def supportedUnitsOfMeasureList = supportedUnitsOfMeasure.tokenize(',[]') as LinkedList

def supportedUnitsOfMeasureCurrent = supportedUnitsOfMeasureList.size() - 1
testRunner.testCase.setPropertyValue( "supportedUnitsOfMeasureCurrent" , "$supportedUnitsOfMeasureCurrent" )

def unitOfMeasure = supportedUnitsOfMeasureList[supportedUnitsOfMeasureCurrent]

//make sure we remove leading space
unitOfMeasure = unitOfMeasure.stripIndent()

testRunner.testCase.setPropertyValue( "unitOfMeasure" , "$unitOfMeasure" )

log.info "unitOfMeasure: " + unitOfMeasure
log.info "supportedUnitsOfMeasureCurrent: " + supportedUnitsOfMeasureCurrent
log.info "defaultUnitOfMeasure: " + defaultUnitOfMeasure

if (unitOfMeasure == "piece"){
	testRunner.testCase.setPropertyValue( "quantityPrecision" , "0" )
} else if (unitOfMeasure == "kg"){
	testRunner.testCase.setPropertyValue( "quantityPrecision" , "3" )
} else {
	context.testRunner.fail("New unitOfMeasure is added $unitOfMeasure")
}

def random = (Math.abs(new Random().nextInt() % 1000000000) + 1).toString()
testRunner.testCase.setPropertyValue( "random" , random ) testRunner.testCase.setPropertyValue( "sku" , random ) testRunner.testCase.setPropertyValue( "name" , "Test product without price $random") testRunner.testCase.setPropertyValue( "description" , "This is description of test product without price $random. Best product ever!")
log.info "response is $random"

def random = (Math.abs(new Random().nextInt() % 1000000000) + 1).toString()
def previousRandom = testRunner.testCase.getPropertyValue("random")

//Make product2 sku to consist of product1 sku and some text
testRunner.testCase.setPropertyValue( "sku2" , "TestSku$previousRandom$random" )
testRunner.testCase.setPropertyValue( "name2" , "Test product without price $random")
testRunner.testCase.setPropertyValue( "description2" , "This is description of test product without price $random. Best product ever!")


log.info "response   is $random"

