con:configuration.scriptText:
def today =new Date().format('yyyy-MM-dd')
log.info today

def response = context.expand('${Product - get recently updated products#Response}')

def datesModified = context.expand('${Product - get recently updated products#Response#$[\'response\'][*][\'dateModified\']}')

def datesModifiedList = datesModified.tokenize(',[]') as LinkedList

for (def dateModified in datesModifiedList) {
	//make sure we remove leading space
	dateModified = dateModified.stripIndent()
	log.info dateModified
	assert dateModified.substring(0,10) == today 
}

con:config.script:
def supportedUnitsOfMeasureCurrent = testRunner.testCase.getPropertyValue("supportedUnitsOfMeasureCurrent") as Integer

if (supportedUnitsOfMeasureCurrent > 0) {

	supportedUnitsOfMeasureCurrent--
	testRunner.testCase.setPropertyValue( "supportedUnitsOfMeasureCurrent" , "$supportedUnitsOfMeasureCurrent" )

	def supportedUnitsOfMeasure = testRunner.testCase.getPropertyValue("supportedUnitsOfMeasure")
	def supportedUnitsOfMeasureList = supportedUnitsOfMeasure.tokenize(',[]') as LinkedList
	def unitOfMeasure = supportedUnitsOfMeasureList[supportedUnitsOfMeasureCurrent]
	//make sure we remove leading space
	unitOfMeasure = unitOfMeasure.stripIndent()

	testRunner.testCase.setPropertyValue( "unitOfMeasure" , "$unitOfMeasure" )

	if (unitOfMeasure == "piece"){
	testRunner.testCase.setPropertyValue( "quantityPrecision" , "0" )
	} else if (unitOfMeasure == "kg"){
		testRunner.testCase.setPropertyValue( "quantityPrecision" , "3" )
	} else {
		context.testRunner.fail("New unitOfMeasure is added $unitOfMeasure")
	}

	log.info "unitOfMeasure: " + unitOfMeasure
	log.info "supportedUnitsOfMeasureCurrent: " + supportedUnitsOfMeasureCurrent


	testRunner.gotoStepByName('Product - Get products Count')	
} else {
	log.info "Existing unitOfmeasure loop"
}
