con:config.script:
def ds = testRunner.testCase.testSteps["DataSource - load product details from csv"];

def name = ds.getPropertyValue("name")
testRunner.testCase.setPropertyValue( "name" , name)

def sku = ds.getPropertyValue("sku")
testRunner.testCase.setPropertyValue( "sku" , sku)

def unitOfMeasure = ds.getPropertyValue("unitOfMeasure")
testRunner.testCase.setPropertyValue( "unitOfMeasure" , unitOfMeasure)

if (unitOfMeasure == "piece"){
	testRunner.testCase.setPropertyValue( "quantityPrecision" , "0" )
} else if (unitOfMeasure == "kg"){
	testRunner.testCase.setPropertyValue( "quantityPrecision" , "3" )
} else {
	context.testRunner.fail("New unitOfMeasure is added $unitOfMeasure")
}

def isService = ds.getPropertyValue("isService")
testRunner.testCase.setPropertyValue( "isService" , isService)

def requiresUniqueId = ds.getPropertyValue("requiresUniqueId")
testRunner.testCase.setPropertyValue( "requiresUniqueId" , requiresUniqueId)

def sellable = ds.getPropertyValue("sellable")
testRunner.testCase.setPropertyValue( "sellable" , sellable)

def visible = ds.getPropertyValue("visible")
testRunner.testCase.setPropertyValue( "visible" , visible)

def isStocked = ds.getPropertyValue("isStocked")
testRunner.testCase.setPropertyValue( "isStocked" , isStocked)

def ds = testRunner.testCase.testSteps["DataSource - load prices details from cvs"];

def unitOfMeasure =  testRunner.testCase.getPropertyValue("unitOfMeasure")

def flexible= ds.getPropertyValue("flexible")
testRunner.testCase.setPropertyValue( "flexible" , flexible)
if (unitOfMeasure != "piece" &&  flexible == "true")
{
	testRunner.gotoStepByName('DataSource Loop - prices')	
} 

def countryCode = ds.getPropertyValue("countryCode")
testRunner.testCase.setPropertyValue( "countryCode" , countryCode)

def currencyCode = ds.getPropertyValue("currencyCode")
testRunner.testCase.setPropertyValue( "currencyCode" , currencyCode)

def centAmountExcludingVAT = ds.getPropertyValue("centAmountExcludingVAT")
testRunner.testCase.setPropertyValue( "centAmountExcludingVAT" , centAmountExcludingVAT)

def taxPercentage = ds.getPropertyValue("taxPercentage")
testRunner.testCase.setPropertyValue( "taxPercentage" , taxPercentage)

def minCentAmountExcludingVAT = ds.getPropertyValue("minCentAmountExcludingVAT")
testRunner.testCase.setPropertyValue( "minCentAmountExcludingVAT" , minCentAmountExcludingVAT)

def maxCentAmountExcludingVAT = ds.getPropertyValue("maxCentAmountExcludingVAT")
testRunner.testCase.setPropertyValue( "maxCentAmountExcludingVAT" , maxCentAmountExcludingVAT)

def flexible =  testRunner.testCase.getPropertyValue("flexible")
log.info flexible

if (flexible == "true"){
	log.info "Will create flexible price"
	testRunner.runTestStepByName('Price - Create flexible price for product')	
} else if (flexible == "false"){
	log.info "Will create non flexible price"
	testRunner.runTestStepByName('Price - Create non flexible price for product')	
} else {
	context.testRunner.fail("Incorrect data for flexible param is loaded: $flexible")
}
