con:config.script:
testRunner.testCase.setPropertyValue("paymentConfigId", "")
testRunner.testCase.setPropertyValue("nonIntegratedPaymentMethodName", "")

def method = testRunner.testCase.testSuite.getPropertyValue("method").toString()
def requiresReference = testRunner.testCase.getPropertyValue("requiresReference").toString()

if (requiresReference == "true"){
	def randomNo = (Math.abs(new Random().nextInt() % 10000000000) + 1).toString()
	def details = "Test Automation $randomNo"
	testRunner.testCase.setPropertyValue( "details" , details )
}else {
	def details = ""
	testRunner.testCase.setPropertyValue( "details" , details )
}

def paymentMethods = context.expand('${Payment - get transaction handshake#Response#$[\'response\'][\'availablePaymentMethods\'][*][\'paymentMethod\']}')
def paymentMethodsList = paymentMethods.tokenize(',[]') as LinkedList

for (int i = 0; i < paymentMethodsList.size(); i++){
	paymentMethodsList[i] = paymentMethodsList[i].trim()
	if (paymentMethodsList[i] == method){
		def path = "\${Payment - get transaction handshake#Response#\$[\'response\'][\'availablePaymentMethods\'][" + i.toString() + "][\'config\'][\'requiresReference\']}"
		def requiresReferenceTemp = context.expand(path).toString()
		if (requiresReferenceTemp == requiresReference){
			def pathName = "\${Payment - get transaction handshake#Response#\$[\'response\'][\'availablePaymentMethods\'][" + i.toString() + "][\'config\'][\'name\']}"
			def nonIntegratedPaymentMethodName = context.expand(pathName).toString()
			testRunner.testCase.setPropertyValue("nonIntegratedPaymentMethodName", nonIntegratedPaymentMethodName)
			log.info "nonIntegratedPaymentMethodName: "+nonIntegratedPaymentMethodName.toString()

			def pathConfigId = "\${Payment - get transaction handshake#Response#\$[\'response\'][\'availablePaymentMethods\'][" + i.toString() + "][\'paymentConfigId\']}"
			def paymentConfigId = context.expand(pathConfigId).toString()
			testRunner.testCase.setPropertyValue("paymentConfigId", paymentConfigId)
			log.info "paymentConfigId: "+paymentConfigId.toString()

			break;
		}
	}
}

def paymentConfigId = testRunner.testCase.getPropertyValue("paymentConfigId").toString()
def nonIntegratedPaymentMethodName = testRunner.testCase.getPropertyValue("nonIntegratedPaymentMethodName").toString()

if (paymentConfigId == "" || nonIntegratedPaymentMethodName == "" ){
	context.testRunner.fail("paymentConfigId or nonIntegratedPaymentMethodName is empty")    	
}

def response = context.expand('${Store - Get all my Stores#Response]}')

def storeIds = context.expand('${Store - Get all my Stores#Response#$[\'response\'][*][\'storeId\']}')
def storeNames = context.expand('${Store - Get all my Stores#Response#$[\'response\'][*][\'name\']}')


def storeIdsList = storeIds.tokenize(',[]') as LinkedList
def storeNamesList = storeNames.tokenize(',[]') as LinkedList
def size = storeIdsList.size()

def automationCreatedStore = true
def automationCreatedStoreName = testRunner.testCase.testSuite.project.getTestSuiteByName("Store").getPropertyValue("storeName").toString()
log.info automationCreatedStoreName

while (automationCreatedStore){
	def randomStore = (Math.abs(new Random().nextInt() % size) + 1) - 1 as int
	def randomStoreName = storeNamesList[randomStore].toString()
	if (randomStoreName.matches("(.*)$automationCreatedStoreName(.*)")){
		automationCreatedStore = true
	} else {
		def randomStoreId = storeIdsList[randomStore]
		automationCreatedStore = false
		testRunner.testCase.setPropertyValue("storeId", randomStoreId)
		log.info randomStoreId
		log.info randomStoreName
	}
}

